/*********************************************************************
*
*       _GetStringA()
*
*  Function description
*    Extract US-ASCII string from UTF-16 string.
*
*  Parameters
*    pOutput     - Pointer to output string.
*    pOutputLen  - [IN]  Octet capacity of output string.
*                  [OUT] Octet lngth of output string.
*    pInput      - Pointer to UTF-16 LE octet string.
*    InputLen    - Octet length of the UTF-16 LE octet string.
*    Replacement - Character that replaces any non-US-ASCII character
*                  in the input string.
*/
static void _GetStringA(char *pOutput, unsigned *pOutputLen, U8 *pInput, unsigned InputLen, unsigned Replacement) {
  unsigned OutputLen;
  unsigned Char;
  //
  // First two bytes of descriptor are type and length.
  //
  if (InputLen > 2) {
    pInput   += 2;
    InputLen -= 2;
  }
  OutputLen = 0;
  while (OutputLen < *pOutputLen && InputLen > 1) {
    Char = USBH_LoadU16LE(pInput);
    if (Char >= 0x7E) {
      Char = Replacement;
    }
    if (Char) {
      *pOutput = Char & 0xFF;
      pOutput   += 1;
      OutputLen += 1;
    }
    pInput   += 2;
    InputLen -= 2;
  }
  //
  *pOutputLen = OutputLen;
}
#endif
/*********************************************************************
*
*       _MIDI_DeviceEvent()
*
*  Function description
*    Called when a device is added or removed.
*
*  Parameters
*    pContext - Pointer to user-provided context.
*    DevIndex - Zero-based device index.
*    Event    - Reason for callback.
*
*  Additional information
*    Called in the context of the USBH task.
*/
static void _MIDI_DeviceEvent(void * pContext, U8 DevIndex, USBH_DEVICE_EVENT Event) {
  USBH_MIDI_DEVICE_INFO DevInfo;
  USBH_STATUS           Status;
  unsigned              i;
  //
  (void)pContext;
  //
  switch (Event) {
  case USBH_DEVICE_EVENT_ADD:
    if (_aMidiDevice[DevIndex] != USBH_MIDI_INVALID_HANDLE) {
      USBH_Warnf_Application("MIDI device %d already open!", DevIndex);
    } else {
      _aMidiDevice[DevIndex] = USBH_MIDI_Open(DevIndex);
      if (_aMidiDevice[DevIndex] != USBH_MIDI_INVALID_HANDLE) {
        Status = USBH_MIDI_GetDeviceInfo(_aMidiDevice[DevIndex], &DevInfo);
        if (Status == USBH_STATUS_SUCCESS) {
          USBH_Logf_Application("MIDI device %d added: %u In cables, %u Out cables", DevIndex, DevInfo.NumInCables, DevInfo.NumOutCables);
        } else {
          USBH_Warnf_Application("Error getting MIDI device %d information!", DevIndex);
        }
      } else {
        USBH_Warnf_Application("Error opening MIDI device %d!", DevIndex);
      }
    }
    break;
    //
  case USBH_DEVICE_EVENT_REMOVE:
    if (_aMidiDevice[DevIndex] == USBH_MIDI_INVALID_HANDLE) {
      USBH_Warnf_Application("MIDI device %d already removed!", DevIndex);
    } else {
      (void)USBH_MIDI_Close(_aMidiDevice[DevIndex]);
      _aMidiDevice[DevIndex] = USBH_MIDI_INVALID_HANDLE;
      USBH_Logf_Application("MIDI device %d removed", DevIndex);
    }
    break;
    //
  default:
    break;
  }
  //
  // Now get the device and manufacturer name of the "primary" device.
  //
  if (Event == USBH_DEVICE_EVENT_ADD) {
    _ManufacturerNameLen = 0;
    _ProductNameLen      = 0;
    //
    for (i = 0; i < SEGGER_COUNTOF(_aMidiDevice); ++i) {
      if (_aMidiDevice[i] != USBH_MIDI_INVALID_HANDLE) {
        USBH_INTERFACE_HANDLE hInterface;
        U8                    aDesc[18];
        unsigned              DescLen;
        U8                    iManufacturer;
        U8                    iProduct;
        //
        USBH_MIDI_GetDeviceInfo(_aMidiDevice[i], &DevInfo);
        Status = USBH_OpenInterface(DevInfo.InterfaceId, 0, &hInterface);
        if (Status == USBH_STATUS_SUCCESS) {
          Status = USBH_GetDeviceDescriptor(hInterface, aDesc, &DescLen);
        }
        if (Status == USBH_STATUS_SUCCESS) {
          iManufacturer = aDesc[14];
          iProduct      = aDesc[15];
          _ManufacturerNameLen = sizeof(_aManufacturerName);
          Status = USBH_GetStringDescriptor(hInterface, iManufacturer, 0, _aManufacturerName, &_ManufacturerNameLen);
          if (Status != USBH_STATUS_SUCCESS) {
            _ManufacturerNameLen = 0;
          }
          _ProductNameLen = sizeof(_aProductName);
          Status = USBH_GetStringDescriptor(hInterface, iProduct, 0, _aProductName, &_ProductNameLen);
          if (Status != USBH_STATUS_SUCCESS) {
            _ProductNameLen = 0;
          }
        }
        USBH_CloseInterface(hInterface);
      }
    }
    //
    _NoteChange = 1;
  }
}

