/*********************************************************************
*
*       _PlayAPI_Delta()
*
*  Function description
*    Process MIDI file Delta.
*
*  Parameters
*    pContext - Pointer to user context.
*    Delta    - Number of delta-ticks before next event.
*
*  Additional information
*    This function waits until the note should be sounded or the lyric
*    displayed using a 64-bit 1us high-precision timer that is readily
*    available in embOS.  It does so without using a busy-loop on
*    a timer.
*/
static void _PlayAPI_Delta(void *pContext, U32 Delta) {
  PLAY_CONTEXT * pSelf;
  U64            AbsMicroseconds;
  I64            DeltaMicroseconds;
  //
  pSelf = (PLAY_CONTEXT *)pContext;
  //
  if (_FirstDelta) {
    _OriginTime = OS_GetTime_us64();
    _FirstDelta = 0;
  }
  if (pSelf->CurTrackId == pSelf->PlayTrackId) {
    pSelf->PlayHead += Delta;
    AbsMicroseconds  = pSelf->PlayHead;
    AbsMicroseconds *= pSelf->Tempo;      // Tempo is microseconds per quarter note
    AbsMicroseconds /= pSelf->Division;   // Division is delta-ticks per quarter note
    AbsMicroseconds += _OriginTime;       // Number of microseconds since play commenced
    //
    // Compute microseconds until the event fires.
    //
    DeltaMicroseconds = (I64)(AbsMicroseconds - OS_GetTime_us64());
    //
    // Coarse millisecond-based delay.  We delay until there is approximately
    // 3ms until the event expires, leaving us some margin because we don't
    // exactly know when the system tick will happen: could be immediately,
    // or could be in 999us.
    //
    if (DeltaMicroseconds > 3000) {
      OS_Delay((unsigned)(DeltaMicroseconds / 1000));
    }
    //
    // The event fires within 3ms, or 3,000 microseconds which
    // we can pass to embOS to sort out for us.  It does this
    // precisely.
    //
    DeltaMicroseconds = (I64)(AbsMicroseconds - OS_GetTime_us64());
    if (DeltaMicroseconds > 0) {
      OS_Delayus((unsigned)DeltaMicroseconds);
    }
  }
}

/*********************************************************************
*
*       _PlayAPI_MidiEvent()
*
*  Function description
*    Send MIDI event to all attached devices.
*
*  Parameters
*    pContext - Pointer to user context.
*    pData    - Pointer to MIDI message octet string.
*    DataLen  - Octet length of the MIDI message octet string.
*
*  Additional information
*    This function also updates the state of the virtual MIDI
*    keyboard and signals to the display task that a redraw
*    is necessary.
*/
static void _PlayAPI_MidiEvent(void *pContext, const U8 *pData, unsigned DataLen) {
  PLAY_CONTEXT * pSelf;
  U32            Event;
  unsigned       i;
  //
  pSelf = (PLAY_CONTEXT *)pContext;
  //
  if (pSelf->CurTrackId == pSelf->PlayTrackId) {
    Event = MIDI_ConstructEvent(pData, DataLen);
    Event |= ((Event & 0x00F00000) << 4);  // Copy status bits to CID
    //
    // Update keyboard with event
    //
    if ((MIDI_STATUS(Event) & 0xF0) == 0x90) {  // Note On
      _aNoteOn[MIDI_DATA2(Event)] = MIDI_DATA3(Event) != 0;  // Velocity 0 is note off
      _NoteChange = 1;
    } else if ((MIDI_STATUS(Event) & 0xF0) == 0x80) {  // Note Off
      _aNoteOn[MIDI_DATA2(Event)] = 0;
      _NoteChange = 1;
    }
    //
    for (i = 0; i < SEGGER_COUNTOF(_aMidiDevice); ++i) {
      if (_aMidiDevice[i] != USBH_MIDI_INVALID_HANDLE) {
        USBH_MIDI_WrEvent(_aMidiDevice[i], Event);
      }
    }
  }
}

