/*********************************************************************
*
*       _PlayAPI_Lyric()
*
*  Function description
*    Process Lyric event.
*
*  Parameters
*    pContext - Pointer to user context.
*    pText    - Pointer to lyric octet string.
*    TextLen  - Octet length of the lyric octet string.
*/
static void _PlayAPI_Lyric(void *pContext, const U8 *pText, unsigned TextLen) {
  PLAY_CONTEXT * pSelf;
  unsigned       i;
  //
  pSelf = (PLAY_CONTEXT *)pContext;
  //
  if (pSelf->CurTrackId == pSelf->PlayTrackId) {
    for (i = 0; i < TextLen; ++i) {
      if (pText[i] == '\r' || pText[i] == '\n') {
        pSelf->LyricPos = 0;
      } else if (pText[i] == '_') {
        pSelf->aLyric[pSelf->LyricPos++] = ' ';
        pSelf->aLyric[pSelf->LyricPos] = 0;
      } else {
        pSelf->aLyric[pSelf->LyricPos++] = pText[i];
        pSelf->aLyric[pSelf->LyricPos] = 0;
      }
    }
    OS_IncDI();
    memcpy(_aLyric, pSelf->aLyric, 128);
    OS_DecRI();
    _NoteChange = 1;
  }
}

/*********************************************************************
*
*       _WaitingForMIDIDevice()
*
*  Function description
*    Query if MIDI device is plugged in.
*
*  Return value
*    == 0 - No longer waiting, device ready.
*    != 0 - Still waiting.
*/
static int _WaitingForMIDIDevice(void) {
  unsigned i;
  //
  for (i = 0; i < SEGGER_COUNTOF(_aMidiDevice); ++i) {
    if (_aMidiDevice[i] != USBH_MIDI_INVALID_HANDLE) {
      return 0;
    }
  }
  return 1;
}

/*********************************************************************
*
*       _MelodyTask()
*
*  Function description
*    Play the melody track and exit.
*/
static void _MelodyTask(void) {
  PLAY_CONTEXT Context;
  MIDI_FILE    F;
  //
  Context.PlayHead    = 0;
  Context.CurTrackId  = 0;
  Context.Division    = 0;
  Context.PlayTrackId = MELODY_TRACK;
  Context.Tempo       = 0;
  Context.aLyric[0]   = 0;
  Context.LyricPos    = 0;
  //
  while (_WaitingForMIDIDevice()) {
    OS_Delay(100);
  }
  //
  MIDI_PrepareFile     (&F, _aMidiFile, sizeof(_aMidiFile));
  MIDI_ProcessAllTracks(&F, &_PlayAPI, &Context);
  //
  OS_Terminate(0);
}

/*********************************************************************
*
*       _LyricsTask()
*
*  Function description
*    Display the lyrics in time with the music and exit.
*/
static void _LyricsTask(void) {
  PLAY_CONTEXT Context;
  MIDI_FILE    F;
  //
  Context.PlayHead    = 0;
  Context.CurTrackId  = 0;
  Context.Division    = 0;
  Context.PlayTrackId = LYRIC_TRACK;
  Context.Tempo       = 0;
  Context.aLyric[0]   = 0;
  Context.LyricPos    = 0;
  //
  strcpy(_aLyric, "SEGGER emUSB-Host MIDI and emLib-MIDI demo");
  _NoteChange = 1;
  //
  while (_WaitingForMIDIDevice()) {
    OS_Delay(100);
  }
  //
  MIDI_PrepareFile     (&F, _aMidiFile, sizeof(_aMidiFile));
  MIDI_ProcessAllTracks(&F, &_LyricAPI, &Context);
  //
  OS_Terminate(0);
}

